package com.wangwei.spring_init.controller;import com.wangwei.spring_init.bean.Animal;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class AnimalController {    @Autowired    Animal animal;    @RequestMapping("/animal/show")    public String show() {        return animal.toString();    }}