package com.wangwei.spring_init.controller;import com.wangwei.spring_init.bean.Animal;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestControllerpublic class AnimalController {    Logger logger = LoggerFactory.getLogger(AnimalController.class);    @Autowired    Animal animal;    @RequestMapping("/animal/show")    public String show() {        logger.debug("animal show方法调用");        return animal.toString();    }}